import org.marker.mushroom.beans.Menuimport org.marker.mushroom.alias.MenuTypeimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.impl.ArticleModelImplimport java.lang.Classimport groovy.lang.GroovyObject;import org.marker.ext.ModuleUtilsimport org.marker.ext.ModuleActivator;import org.marker.ext.module.ModuleContext;import org.marker.mushroom.ext.model.ContentModel;import org.marker.mushroom.ext.model.ContentModelContext;/** * 文章模块 *  * @author marker */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {	// 存放菜单ID	private int id;	 	/**	 * 启用 	 */	public void start(ModuleContext context) throws Exception { 		// GroovyObject cm = util.loadModelObject("model");				ContentModel cm = new ArticleModelImpl();				ContentModelContext.getInstance().put(cm);								Menu menu = new Menu();		menu.setName("文章管理");		menu.setUrl("article/list.do");		menu.setDescription("管理站内文章信息");		menu.setIcon("fa-th");				MenuUtils menuUtils = MenuUtils.getInstance();				menuUtils.builder(MenuType.L1_CONTENT, menu );		println menu.getId();		this.id = menu.getId();					} 	/**	 * 停用 	 */	public void stop(ModuleContext context) throws Exception { 		ContentModelContext.getInstance().remove("article");						MenuUtils menuUtils =MenuUtils.getInstance();		menuUtils.remove(this.id);			}}